import { ReactEventHandler, ReactNode, useState } from "react";

interface ISideBarItems {
  text: string;
  icons?: ReactNode;
  onClick?: ReactEventHandler;
}

function SideBarItems(props: ISideBarItems) {
  return (
    <div
      className="flex flex-row flex-shrink gap-2 pl-4 pt-[10px] items-center "
      onClick={props.onClick}
    >
      {props.icons}
      <div className=" text-black text-lg font-normal font-['Inter']">
        {props.text}
      </div>
      <div className=""></div>
    </div>
  );
}

